const getPlaylistABI = () => {
	switch (process.env.REACT_APP_CHAIN_ID) {
		case "10":
			return OPplaylistABI;
		case "11155111":
			return TNplaylistABI;
		default:
			return [];
	}
}

const getPlaylistAddress = () => {
	switch (process.env.REACT_APP_CHAIN_ID) {
		case "10":
			return OPplaylistAddress;
		case "11155111":
			return TNplaylistAddress;
		default:
			return "";
	}
}
